// src/services/api.js
import axios from 'axios';

// Function for fetching AI insights
const fetchAIInsights = async (token) => {
  try {
    const response = await fetch("http://localhost:5000/api/tasks/analytics/insights", {
      method: "GET",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    if (!response.ok) {
      throw new Error("Failed to fetch AI insights");
    }
    const data = await response.json();
    return data;
  } catch (err) {
    console.error("Error in fetching AI insights:", err);
    throw err;
  }
};

const fetchProcrastinationInsights = async (token) => {
  try {
    const response = await fetch("http://localhost:5000/api/tasks/insights/procrastination", {
      method: "GET",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    if (!response.ok) {
      throw new Error("Failed to fetch procrastination insights");
    }
    return await response.json();
  } catch (err) {
    console.error("Error fetching procrastination insights:", err);
    throw err;
  }
};

const saveGoalAndReminder = async (taskId, goalData, token) => {
  try {
    const response = await fetch(`http://localhost:5000/api/tasks/${taskId}/goals`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(goalData),
    });
    if (!response.ok) {
      throw new Error("Failed to save goal and reminder");
    }
    return await response.json();
  } catch (err) {
    console.error("Error saving goal and reminder:", err);
    throw err;
  }
};

export { fetchAIInsights, fetchProcrastinationInsights, saveGoalAndReminder };



