// TaskProductivity.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const TaskProductivity = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTaskId, setSelectedTaskId] = useState("");
  const [minutesSpent, setMinutesSpent] = useState("");

  // Fetch tasks on load
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const res = await axios.get("/api/tasks", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        setTasks(res.data);
      } catch (err) {
        console.error("Error fetching tasks:", err);
      }
    };

    fetchTasks();
  }, []);

  // Log time manually
  const handleLogTime = async (e) => {
    e.preventDefault();

    if (!selectedTaskId || !minutesSpent || Number(minutesSpent) <= 0) {
      alert("Please select a task and enter valid time.");
      return;
    }

    try {
      const res = await axios.post(
        `/api/tasks/${selectedTaskId}/log-time`,
        { minutes: Number(minutesSpent) },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      alert(res.data.message);
      setMinutesSpent("");
    } catch (err) {
      console.error("Error logging time:", err);
      alert("Failed to log time.");
    }
  };

  return (
    <div style={{ padding: "1rem" }}>
      <h2>Task Productivity</h2>

      <form onSubmit={handleLogTime}>
        <label>
          Select Task:
          <select
            value={selectedTaskId}
            onChange={(e) => setSelectedTaskId(e.target.value)}
            required
          >
            <option value="">-- Choose a task --</option>
            {tasks.map((task) => (
              <option key={task._id} value={task._id}>
                {task.title}
              </option>
            ))}
          </select>
        </label>

        <br />

        <label>
          Minutes Spent:
          <input
            type="number"
            min="1"
            value={minutesSpent}
            onChange={(e) => setMinutesSpent(e.target.value)}
            required
          />
        </label>

        <br />
        <button type="submit">Log Time</button>
      </form>
    </div>
  );
};

export default TaskProductivity;
