import React from "react";
import { BrowserRouter as Router, Routes, Route, useLocation  } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import About from "./pages/About";
import SignIn from "./pages/SignIn";
import SignUp from "./pages/SignUp";
import CreateTask from "./components/CreateTask";
//import UserDashboard from "./pages/UserDashboard";
import DashboardLayout from './components/Dashboard/DashboardLayout';
import ProfilePage from './components/Dashboard/ProfilePage';
import TasksPage from './components/Dashboard/TasksPage';
import TrackProductivityPage from './components/Dashboard/TrackProductivityPage';
import ProductivityInsightsPage from './components/Dashboard/ProductivityInsightsPage';
import GoalsRemindersPage from './components/Dashboard/GoalsRemindersPage';

const App = () => {
  return (
    <Router>
      <ConditionalNavbar />  {/* ✅ Navbar appears only on Home and About pages */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/signin" element={<SignIn />} />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/create-tasks" element={<CreateTask/>} />

         {/* Dashboard Routes */}
         <Route path="/dashboard" element={<DashboardLayout />}>
          <Route path="profile" element={<ProfilePage />} />
          <Route path="tasks" element={<TasksPage />} />
          <Route path="track-productivity" element={<TrackProductivityPage />} />
          <Route path="productivity-insights" element={<ProductivityInsightsPage />} />
          <Route path="goals-reminders" element={<GoalsRemindersPage />} />
        </Route>
      </Routes>
    </Router>
  );
};
// ✅ Function to Show Navbar Only on Home & About Pages
const ConditionalNavbar = () => {
    const location = useLocation();
    return location.pathname === "/"  ? <Navbar /> : null;
  };

export default App;
