// Fetch productivity insights
export const fetchInsights = async (token) => {
    try {
      const response = await api.get("/tasks/analytics/insights", {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Optional: Validate response structure
      if (!response.data) {
        throw new Error("Invalid data structure received from server.");
      }

      return response.data;
    } catch (error) {
      console.error("Error fetching insights:", error.response || error.message);
      throw error;
    }
  };
