// src/pages/Tasks.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Plus, Trash2, Pencil } from 'lucide-react';

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ title: '', description: '', status: 'pending' });

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/tasks');
      setTasks(res.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleCreateClick = () => {
    setFormData({ title: '', description: '', status: 'pending' });
    setSelectedTask(null);
    setShowForm(true);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedTask) {
        await axios.put(`http://localhost:5000/api/tasks/${selectedTask._id}`, formData);
      } else {
        await axios.post('http://localhost:5000/api/tasks', formData);
      }
      fetchTasks();
      setShowForm(false);
    } catch (error) {
      console.error('Error saving task:', error);
    }
  };

  const handleTaskClick = (task) => {
    setSelectedTask(task);
    setShowForm(false);
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure to delete this task?')) {
      try {
        await axios.delete(`http://localhost:5000/api/tasks/${selectedTask._id}`);
        fetchTasks();
        setSelectedTask(null);
      } catch (error) {
        console.error('Error deleting task:', error);
      }
    }
  };

  const handleUpdateClick = () => {
    setFormData({
      title: selectedTask.title,
      description: selectedTask.description,
      status: selectedTask.status,
    });
    setShowForm(true);
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-semibold">Your Tasks</h1>
        <button
          onClick={handleCreateClick}
          className="bg-blue-600 text-white px-4 py-2 rounded flex items-center hover:bg-blue-700"
        >
          <Plus className="mr-2" /> Create Task
        </button>
      </div>

      {showForm && (
        <form onSubmit={handleFormSubmit} className="space-y-4 border p-4 rounded-lg shadow">
          <input
            type="text"
            placeholder="Title"
            value={formData.title}
            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
            required
            className="w-full p-2 border rounded"
          />
          <textarea
            placeholder="Description"
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            className="w-full p-2 border rounded"
          ></textarea>
          <select
            value={formData.status}
            onChange={(e) => setFormData({ ...formData, status: e.target.value })}
            className="w-full p-2 border rounded"
          >
            <option value="pending">Pending</option>
            <option value="in-progress">In Progress</option>
            <option value="completed">Completed</option>
          </select>
          <button
            type="submit"
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            {selectedTask ? 'Update' : 'Create'} Task
          </button>
        </form>
      )}

      <div className="grid grid-cols-1 gap-4 mt-6">
        {tasks.map((task) => (
          <button
            key={task._id}
            onClick={() => handleTaskClick(task)}
            className="w-full text-left p-4 bg-gray-100 hover:bg-gray-200 rounded shadow"
          >
            {task.title}
          </button>
        ))}
      </div>

      {selectedTask && !showForm && (
        <div className="mt-6 border p-4 rounded shadow">
          <h2 className="text-xl font-semibold mb-2">{selectedTask.title}</h2>
          <p className="mb-1"><strong>Description:</strong> {selectedTask.description}</p>
          <p className="mb-1"><strong>Status:</strong> {selectedTask.status}</p>
          <p className="mb-4"><strong>Created:</strong> {new Date(selectedTask.createdAt).toLocaleString()}</p>

          <div className="flex gap-4">
            <button
              onClick={handleUpdateClick}
              className="bg-yellow-500 text-white px-4 py-2 rounded flex items-center hover:bg-yellow-600"
            >
              <Pencil className="mr-2" /> Update
            </button>
            <button
              onClick={handleDelete}
              className="bg-red-600 text-white px-4 py-2 rounded flex items-center hover:bg-red-700"
            >
              <Trash2 className="mr-2" /> Delete
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Tasks;
