// src/components/admin/MonitorProductivity.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import "../../styles/MonitorProductivity.css"; // Optional styling

const MonitorProductivity = () => {
  const [productivityData, setProductivityData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch productivity trends data from backend
    const fetchProductivityData = async () => {
      try {
        const response = await axios.get("/api/admin/productivity");
        setProductivityData(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching productivity data", error);
        setLoading(false);
      }
    };

    fetchProductivityData();
  }, []);

  if (loading) {
    return <div>Loading productivity data...</div>;
  }

  return (
    <div className="monitor-productivity">
      <h1>Monitor Productivity Trends</h1>
      <div className="productivity-reports">
        {productivityData.length === 0 ? (
          <p>No productivity data available.</p>
        ) : (
          <div className="reports">
            {productivityData.map((data, index) => (
              <div key={index} className="report-card">
                <h3>{data.userName}</h3>
                <p>Overall Productivity: {data.productivityScore}%</p>
                <p>Time Spent: {data.timeSpent} hours</p>
                <p>Tasks Completed: {data.tasksCompleted}</p>
                <p>Last Login: {data.lastLogin}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default MonitorProductivity;
