import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/CreateTask.css";  

const CreateTask = () => {
  const [task, setTask] = useState({
    title: "",
    description: "",
    dueDate: "",
    status: "Pending"
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setTask({ ...task, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log("üîÑ Sending data to backend:", task);
      await axios.post("http://localhost:5000/api/tasks", task, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}` // or from context
        }
      });


      alert("‚úÖ Task Created Successfully!");
      setTask({ title: "", description: "", dueDate: "", status: "Pending" });

      navigate("/view-tasks");
    } catch (error) {
      console.error("‚ùå Error creating task:", error.response?.data || error);
      alert("‚ùå Failed to create task. Check console for errors.");
    }
  };

  return (
    <div className="task-form">
      <h2>Create a New Task</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text"
          name="title"
          placeholder="Task Title"
          value={task.title}
          onChange={handleChange}
          required
        />
        <textarea
          name="description"
          placeholder="Task Description"
          value={task.description}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="dueDate"
          value={task.dueDate}
          onChange={handleChange}
          required
        />
        <select name="status" value={task.status} onChange={handleChange}>
          <option value="Pending">Pending</option>
          <option value="In Progress">In Progress</option>
          <option value="Completed">Completed</option>
        </select>
        <button type="submit">Create Task</button>
      </form>
    </div>
  );
};

export default CreateTask;
