import React, { useState } from 'react';
import '../styles/Profile.css'; // Create this file for custom styling

const Profile = () => {
  const [profileImage, setProfileImage] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: '',
    phone: '',
    city: '',
    district: '',
    state: '',
    country: '',
  });

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) setProfileImage(URL.createObjectURL(file));
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Profile data submitted:", formData);
    // You can integrate backend API call here
  };

  return (
    <div className="profile-container">
      <div className="profile-avatar">
        <img
          src={profileImage || 'https://cdn-icons-png.flaticon.com/512/149/149071.png'}
          alt="Profile"
          className="avatar-img"
        />
        <input type="file" accept="image/*" onChange={handleImageChange} />
      </div>

      <form className="profile-form" onSubmit={handleSubmit}>
        <input type="text" name="firstName" placeholder="First Name" value={formData.firstName} onChange={handleInputChange} required />
        <input type="text" name="lastName" placeholder="Last Name" value={formData.lastName} onChange={handleInputChange} required />
        <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleInputChange} required />
        <input type="tel" name="phone" placeholder="Phone Number" value={formData.phone} onChange={handleInputChange} required />
        <input type="text" name="city" placeholder="City" value={formData.city} onChange={handleInputChange} required />
        <input type="text" name="district" placeholder="District" value={formData.district} onChange={handleInputChange} required />
        <input type="text" name="state" placeholder="State" value={formData.state} onChange={handleInputChange} required />
        <input type="text" name="country" placeholder="Country" value={formData.country} onChange={handleInputChange} required />
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default Profile;
