    title: "",
    description: "",
    dueDate: "",
    priority: "Medium"
  });

  const [reminder, setReminder] = useState({
    frequency: "Daily",
    time: "09:00"
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name in goal) {
      setGoal({ ...goal, [name]: value });
    } else if (name in reminder) {
      setReminder({ ...reminder, [name]: value });
    }
  };

