import React, { useState } from 'react';
import '../../styles/Profile.css';

const Profile = () => {
  const [profileImage, setProfileImage] = useState(null);

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: '',
    phone: '',
    city: '',
    district: '',
    state: '',
    country: ''
  });

  const defaultUserIcon = "https://cdn-icons-png.flaticon.com/512/149/149071.png"; // Default icon

  const fieldLabels = {
    firstName: 'First Name',
    lastName: 'Last Name',
    address: 'Address',
    phone: 'Phone Number',
    city: 'City',
    district: 'District',
    state: 'State',
    country: 'Country'
  };

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setProfileImage(URL.createObjectURL(e.target.files[0]));
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert("Profile Updated!");
  };

  return (
    <div className="profile-container">
      <div className="profile-box">
        <div className="image-section">
          <img
            src={profileImage || defaultUserIcon}
            alt="Profile"
            className="profile-img"
          />
          <label htmlFor="profileUpload" className="change-profile-btn">
            {profileImage ? "Change Profile" : "Upload Profile"}
          </label>
          <input
            type="file"
            id="profileUpload"
            accept="image/*"
            onChange={handleImageChange}
            style={{ display: 'none' }}
          />
        </div>

        <form className="form-section" onSubmit={handleSubmit}>
          {Object.entries(formData).map(([key, value]) => (
            <div className="input-group" key={key}>
              <label htmlFor={key} className="field-label">{fieldLabels[key]}</label>
              <input
                id={key}
                type="text"
                name={key}
                placeholder={fieldLabels[key]}
                value={value}
                onChange={handleInputChange}
                required
              />
            </div>
          ))}
          <button type="submit" className="update-btn">Update Profile</button>
        </form>
      </div>
    </div>
  );
};

export default Profile;
