import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();
  const isLoggedIn = localStorage.getItem("token"); // or any key you use for auth

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleLogout = () => {
    localStorage.removeItem("token"); // remove token or clear all
    setIsOpen(false);
    navigate("/signin");
  };

  return (
    <nav className="navbar">
      <div className="left">Productivity Tracker</div>
      <div className={`right ${isOpen ? "open" : ""}`}>
        <Link to="/" onClick={() => setIsOpen(false)}>Home</Link>
        <Link to="/about" onClick={() => setIsOpen(false)}>About</Link>
        {!isLoggedIn ? (
          <>
            <Link to="/signin" onClick={() => setIsOpen(false)}>Sign In</Link>
            {/* <Link to="/signup" onClick={() => setIsOpen(false)}>Sign Up</Link> */}
          </>
        ) : (
          <button
            onClick={handleLogout}
            style={{
              background: "none",
              border: "none",
              color: "#333",
              cursor: "pointer",
              fontSize: "16px",
              padding: "0",
            }}
          >
            Logout
          </button>
        )}
      </div>
      <div className="hamburger" onClick={toggleMenu}>
        <div className="line" />
        <div className="line" />
        <div className="line" />
      </div>
    </nav>
  );
};

export default Navbar;
