// TrackProductivity.jsx
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

const TrackProductivity = () => {
  const [tasks, setTasks] = useState([]);
  const [activeTaskId, setActiveTaskId] = useState(null);
  const [seconds, setSeconds] = useState(0);
  const [isTabActive, setIsTabActive] = useState(true);
  const intervalRef = useRef(null);

  // Fetch tasks
  useEffect(() => {
    const fetchTasks = async () => {
      const token = localStorage.getItem("token");
      const response = await axios.get('http://localhost:5000/api/tasks', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setTasks(response.data);
    };
    fetchTasks();
  }, []);

  // Tab focus tracking
  useEffect(() => {
    const handleBlur = () => {
      setIsTabActive(false);
      if (activeTaskId) alert("‚ö†Ô∏è You're not on the task tab! Stay focused!");
    };
    const handleFocus = () => setIsTabActive(true);

    window.addEventListener("blur", handleBlur);
    window.addEventListener("focus", handleFocus);

    return () => {
      window.removeEventListener("blur", handleBlur);
      window.removeEventListener("focus", handleFocus);
    };
  }, [activeTaskId]);

  // Start Task
  const startTask = (taskId) => {
    setActiveTaskId(taskId);
    setSeconds(0);
    if (intervalRef.current) clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setSeconds((prev) => prev + 1);
    }, 1000);
  };

  const stopTask = async () => {
    if (!activeTaskId) return;
    clearInterval(intervalRef.current);
    intervalRef.current = null;

    const minutesLogged = Math.floor(seconds / 60);
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        `/api/tasks/${activeTaskId}/log-time`,
        { minutes: minutesLogged },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert(`‚úÖ Logged ${minutesLogged} minute(s)`);
    } catch (error) {
      console.error("Error logging time:", error);
      alert("Failed to log time");
    }
    setActiveTaskId(null);
    setSeconds(0);
  };

  return (
    <div>
      <h2>üìà Track Your Productivity</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task._id}>
            <strong>{task.title}</strong> - {task.description}
            {activeTaskId === task._id ? (
              <>
                <p>
                  ‚è±Ô∏è Time Tracked: {Math.floor(seconds / 60)} min {seconds % 60} sec
                </p>
                <button onClick={stopTask}>Stop & Log Time</button>
              </>
            ) : (
              <button onClick={() => startTask(task._id)}>Start Task</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TrackProductivity;
