import React, { useState } from "react";
import axios from "axios";

const LogTimeToTask = ({ taskId }) => {
  const [minutes, setMinutes] = useState(0);

  // Handle form submission to log time for the task
  const handleLogTime = async (e) => {
    e.preventDefault();

    if (minutes <= 0) {
      alert("Please enter a valid number of minutes.");
      return;
    }

    try {
      const response = await axios.post(
        `/api/tasks/${taskId}/log-time`, // Backend endpoint for logging time to task
        { minutes }, // Send the time logged (in minutes) to the backend
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`, // JWT token for authentication
          },
        }
      );
      console.log(response.data); // Log the response from the backend
      alert("Time logged successfully!");
      setMinutes(0); // Reset the input field after logging time
    } catch (error) {
      console.error(error); // Log any errors to the console
      alert("Error logging time.");
    }
  };

  return (
    <div className="log-time-to-task">
      <h3>Log Time for Task: {taskId}</h3>
      <form onSubmit={handleLogTime}>
        <label>
          Minutes:
          <input
            type="number"
            value={minutes}
            onChange={(e) => setMinutes(e.target.value)} // Update minutes state as user types
            min="1"
            required
          />
        </label>
        <button type="submit">Log Time</button>
      </form>
    </div>
  );
};

export default LogTimeToTask;
