import axios from "axios";

// Create an Axios instance with a base URL
const api = axios.create({
  baseURL: "http://localhost:5000/api", // Adjust if your backend runs on a different port
  timeout: 5000,
});

// Fetch productivity insights
export const fetchInsights = async (token) => {
  try {
    const response = await api.get("/tasks/analytics/insights", {
      headers: {
        Authorization: `Bearer ${token}`, // Include token for authentication
      },
    });

    // Optional validation
    if (!response.data) {
      throw new Error("Invalid data structure received from server.");
    }

    return response.data;
  } catch (error) {
    console.error("Error fetching insights:", error.response?.data || error.message);
    throw error;
  }
};

export default api; // Optional: export the api instance if needed elsewhere
