import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  Title,
  Tooltip,
  Legend,
  BarElement,
  CategoryScale,
  LinearScale,
} from "chart.js";
import { fetchAIInsights } from "../services/api";
import "../../styles/ProductivityInsights.css"; // optional external CSS

// Register Chart.js components
ChartJS.register(Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale);

const ProductivityInsights = () => {
  const [insights, setInsights] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      setError("Authentication token is missing.");
      setLoading(false);
      return;
    }

    fetchAIInsights(token)
      .then((data) => {
        setInsights(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching insights", err);
        setError("Failed to fetch insights. Please try again.");
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading AI insights...</p>;
  if (error) return <p className="text-red-500">{error}</p>;
  if (!insights || !insights.tasks || insights.tasks.length === 0)
    return <p>No insights available at the moment.</p>;

  // Prepare chart data
  const barData = {
    labels: insights.tasks.map((task) => task.date),
    datasets: [
      {
        label: "Time Spent (minutes)",
        data: insights.tasks.map((task) => task.timeSpent),
        backgroundColor: "rgba(59, 130, 246, 0.6)",
        borderColor: "rgba(59, 130, 246, 1)",
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "Time Spent Per Task (by Date)",
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 10,
        },
      },
    },
  };

  return (
    <div className="productivity-insights-container">
      <h2 className="insights-title">AI Productivity Insights</h2>
      <Bar data={barData} options={options} />
    </div>
  );
};

export default ProductivityInsights;
