// src/components/ProductivityInsights.jsx
import React, { useEffect, useState } from "react";
import {
  fetchProductivityTrends,
  fetchProductivityScore,
  fetchTaskSuggestions,
  fetchProcrastinationAlerts,
} from "../services/api";

const ProductivityInsights = ({ token }) => {
  const [trends, setTrends] = useState(null);
  const [score, setScore] = useState(null);
  const [suggestions, setSuggestions] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [trendData, scoreData, suggestionData, alertData] = await Promise.all([
          fetchProductivityTrends(token),
          fetchProductivityScore(token),
          fetchTaskSuggestions(token),
          fetchProcrastinationAlerts(token),
        ]);

        setTrends(trendData);
        setScore(scoreData);
        setSuggestions(suggestionData);
        setAlerts(alertData);
        setLoading(false);
      } catch (err) {
        setError("Failed to load AI insights");
        setLoading(false);
      }
    };

    if (token) {
      fetchData();
    } else {
      setError("Authentication token missing.");
      setLoading(false);
    }
  }, [token]);

  if (loading) return <p>Loading AI insights...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="p-4 bg-white rounded-xl shadow-md space-y-6">
      <h2 className="text-2xl font-bold mb-4">ðŸ§  AI Productivity Insights</h2>

      {/* Productivity Trends */}
      <div className="bg-blue-50 p-4 rounded-lg">
        <h3 className="text-lg font-semibold mb-2">ðŸ“Š Productivity Trends</h3>
        <p>{trends?.summary || "No trend data available."}</p>
      </div>

      {/* Productivity Score */}
      <div className="bg-green-50 p-4 rounded-lg">
        <h3 className="text-lg font-semibold mb-2">ðŸ“ˆ Predicted Productivity Score</h3>
        <p className="text-3xl font-bold text-green-600">{score?.value || "N/A"} / 100</p>
        <p className="text-sm text-gray-600">{score?.explanation}</p>
      </div>

      {/* Task Suggestions */}
      <div className="bg-yellow-50 p-4 rounded-lg">
        <h3 className="text-lg font-semibold mb-2">âœ… Suggested Task Optimizations</h3>
        <ul className="list-disc ml-6 space-y-1">
          {suggestions.length > 0 ? (
            suggestions.map((item, idx) => <li key={idx}>{item}</li>)
          ) : (
            <li>No suggestions at the moment.</li>
          )}
        </ul>
      </div>

      {/* Procrastination Alerts */}
      <div className="bg-red-50 p-4 rounded-lg">
        <h3 className="text-lg font-semibold mb-2">ðŸš¨ Procrastination Alerts</h3>
        <ul className="list-disc ml-6 space-y-1">
          {alerts.length > 0 ? (
            alerts.map((alert, idx) => <li key={idx}>{alert}</li>)
          ) : (
            <li>No procrastination patterns detected.</li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default ProductivityInsights;
