// src/components/Dashboard/Tasks.jsx
import React, { useState } from 'react';
import '../../styles/Tasks.css';

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '', status: 'pending' });
  const [editIndex, setEditIndex] = useState(null);

  const handleChange = (e) => {
    setNewTask({ ...newTask, [e.target.name]: e.target.value });
  };

  const handleCreate = () => {
    if (newTask.title.trim() === '') return;
    setTasks([...tasks, { ...newTask, createdAt: new Date().toLocaleString() }]);
    setNewTask({ title: '', description: '', status: 'pending' });
  };

  const handleUpdate = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index] = { ...newTask, createdAt: updatedTasks[index].createdAt };
    setTasks(updatedTasks);
    setNewTask({ title: '', description: '', status: 'pending' });
    setEditIndex(null);
  };

  const handleEdit = (index) => {
    setNewTask(tasks[index]);
    setEditIndex(index);
  };

  const handleDelete = (index) => {
    if (window.confirm("Are you sure you want to delete this task?")) {
      const updatedTasks = tasks.filter((_, i) => i !== index);
      setTasks(updatedTasks);
    }
  };

  return (
    <div className="tasks-container">
      <h3>Task Manager</h3>

      <div className="task-form">
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={newTask.title}
          onChange={handleChange}
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newTask.description}
          onChange={handleChange}
        />
        <select name="status" value={newTask.status} onChange={handleChange}>
          <option value="pending">Pending</option>
          <option value="in-progress">In Progress</option>
          <option value="completed">Completed</option>
        </select>
        {editIndex !== null ? (
          <button onClick={() => handleUpdate(editIndex)}>Update Task</button>
        ) : (
          <button onClick={handleCreate}>Create Task</button>
        )}
      </div>

      {tasks.length === 0 ? (
        <div className="empty-state">
          <p>No tasks available. Click "+" to create a task.</p>
        </div>
      ) : (
        <div className="task-list">
          {tasks.map((task, index) => (
            <div key={index} className={`task-card ${task.status}`}>
              <h4>{task.title}</h4>
              <p>{task.description}</p>
              <p>Status: <strong>{task.status}</strong></p>
              <p><small>Created: {task.createdAt}</small></p>
              <div className="task-actions">
                <button onClick={() => handleEdit(index)}>Edit</button>
                <button onClick={() => handleDelete(index)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Tasks;
