// Blogs.js

import React from "react";
import "../styles/Blogs.css"; // we'll create this CSS too

const blogTips = [
  {
    title: "Stay Consistent",
    description: "Consistency is key to achieving your goals. Set small daily targets.",
  },
  {
    title: "Prioritize Tasks",
    description: "Use the Eisenhower Matrix to decide on and prioritize tasks.",
  },
  {
    title: "Take Regular Breaks",
    description: "Following the Pomodoro technique can help maintain productivity.",
  },
  {
    title: "Use Technology Wisely",
    description: "Use apps and tools that enhance your productivity, not distract you.",
  },
];

const Blogs = () => {
  return (
    <div className="blogs-container">
      <h1>Productivity Tips</h1>
      <div className="cards-wrapper">
        {blogTips.map((tip, index) => (
          <div className="card" key={index}>
            <h2>{tip.title}</h2>
            <p>{tip.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blogs;
