import React from "react";
import { Link } from "react-router-dom"; // Import Link for navigation
import "../styles/Home.css";
import Testimonials from "./Testimonials";

const Home = () => {
  return (
    <div className="home-section">


      <div className="hero" id="home">
        <br />
        <br />
        <h1>Welcome to Smart Productivity Tracker</h1>
        <p>Track your time, avoid distractions, and boost productivity.</p>
        {/* Add Get Started button */}
        <Link to="/signup">
          <button className="get-started-btn">Get Started</button>
        </Link>
      </div>

      <Testimonials />

      <div className="section" id="about">
        <h2>About Us</h2>
        <p>
          Smart Productivity Tracker is designed to help individuals and teams
          manage their time efficiently. With AI-powered insights and smart task
          organization, staying productive has never been easier.
        </p>
      </div>

      <div className="section" id="contact">
        <h2>Contact</h2>
        <p>
          If you have any questions or feedback, feel free to reach out to us at
          <strong> support@productivitytracker.com</strong><br />
          +91 7396299057.
        </p>
      </div>
    </div>
  );
};

export default Home;
