import React, { useState } from 'react';
import './Profile.css';
import defaultUser from '../assets/default-user.png'; // Ensure this file exists

const Profile = () => {
  const [profileImage, setProfileImage] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: '',
    phone: '',
    city: '',
    district: '',
    state: '',
    country: ''
  });

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfileImage(URL.createObjectURL(file));
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData); // You can replace this with an API call
    alert("Profile Updated!");
  };

  return (
    <div className="profile-container">
      <div className="profile-box">
        <div className="image-section">
          <img
            src={profileImage || defaultUser}
            alt="Profile"
            className="profile-img"
          />
          <label htmlFor="profileUpload" className="change-profile-btn">
            {profileImage ? "Change Profile" : "Upload Profile"}
          </label>
          <input
            type="file"
            id="profileUpload"
            onChange={handleImageChange}
            accept="image/*"
            style={{ display: 'none' }}
          />
        </div>

        <form className="form-section" onSubmit={handleSubmit}>
          {Object.entries(formData).map(([key, value]) => (
            <input
              key={key}
              type="text"
              name={key}
              value={value}
              onChange={handleInputChange}
              placeholder={key
                .replace(/([A-Z])/g, ' $1')
                .replace(/^./, str => str.toUpperCase())}
              required
            />
          ))}
          <button type="submit" className="update-btn">Update Profile</button>
        </form>
      </div>
    </div>
  );
};

export default Profile;
