import React, { useEffect, useState } from "react";
import {
  fetchProductivityTrends,
  fetchProductivityScore,
  fetchTaskOptimizations,
  fetchProcrastinationAlerts,
} from "../services/api";

const ProductivityInsights = ({ token }) => {
  const [trends, setTrends] = useState(null);
  const [score, setScore] = useState(null);
  const [optimizations, setOptimizations] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAIInsights = async () => {
      try {
        const [trendData, scoreData, optimizationData, alertData] = await Promise.all([
          fetchProductivityTrends(token),
          fetchProductivityScore(token),
          fetchTaskOptimizations(token),
          fetchProcrastinationAlerts(token),
        ]);

        setTrends(trendData);
        setScore(scoreData);
        setOptimizations(optimizationData);
        setAlerts(alertData);
        setLoading(false);
      } catch (err) {
        console.error("Error loading AI insights:", err);
        setError("Failed to load insights. Try again later.");
        setLoading(false);
      }
    };

    fetchAIInsights();
  }, [token]);

  if (loading) return <p>Loading AI-generated insights...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="p-6 bg-white rounded-xl shadow-lg space-y-6">
      <h2 className="text-2xl font-bold text-gray-800">üß† AI Productivity Insights</h2>

      <div>
        <h3 className="text-lg font-semibold text-gray-700">üìä Productivity Trends</h3>
        <p className="text-gray-600">{trends?.description || "No trend data available."}</p>
      </div>

      <div>
        <h3 className="text-lg font-semibold text-gray-700">‚ö° Productivity Score</h3>
        <p className="text-gray-600 text-xl font-medium">
          {score?.score ?? "N/A"} / 100
        </p>
      </div>

      <div>
        <h3 className="text-lg font-semibold text-gray-700">üí° Task Optimizations</h3>
        {optimizations.length ? (
          <ul className="list-disc pl-5 text-gray-600">
            {optimizations.map((tip, index) => (
              <li key={index}>{tip}</li>
            ))}
          </ul>
        ) : (
          <p>No recommendations at the moment.</p>
        )}
      </div>

      <div>
        <h3 className="text-lg font-semibold text-gray-700">‚è≥ Procrastination Alerts</h3>
        {alerts.length ? (
          <ul className="list-disc pl-5 text-red-600">
            {alerts.map((alert, index) => (
              <li key={index}>{alert}</li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-500">No procrastination detected. Good job!</p>
        )}
      </div>
    </div>
  );
};

export default ProductivityInsights;
