// Blogs.js

import React, { useState } from "react";
import "../styles/Blogs.css"; // Update your CSS as well

const blogTips = [
  { 
    title: "Stay Consistent", 
    short: "Build small habits daily for success.", 
    full: "Consistency compounds over time. Even small efforts, when repeated daily, can lead to incredible results. Start with simple, achievable goals and stick to them!" ,
    image: "📅"
  },
  { 
    title: "Prioritize Tasks", 
    short: "Focus on important, not just urgent tasks.", 
    full: "Use techniques like the Eisenhower Matrix to classify tasks into important and urgent, important but not urgent, and so on. Focus on the 'important' quadrant for real progress.",
    image: "✅"
  },
  { 
    title: "Take Regular Breaks", 
    short: "Work smarter with scheduled breaks.", 
    full: "The Pomodoro Technique (25 minutes work + 5 minutes break) can prevent burnout and keep your mind fresh and alert throughout the day.",
    image: "⏰"
  },
  { 
    title: "Use Technology Wisely", 
    short: "Tech should empower, not distract.", 
    full: "Choose apps and tools that help you stay productive like Todoist, Trello, or Notion, and avoid mindless scrolling on social media.",
    image: "📱"
  },
  { 
    title: "Set SMART Goals", 
    short: "Be clear and specific about your goals.", 
    full: "SMART goals are Specific, Measurable, Achievable, Relevant, and Time-bound. Defining your goals this way increases your chances of success.",
    image: "🎯"
  },
  { 
    title: "Reflect Daily", 
    short: "Self-review sharpens your focus.", 
    full: "At the end of each day, spend 5 minutes reviewing what you achieved and what you could improve. This will help you stay aligned with your goals.",
    image: "📝"
  },
];

const Blogs = () => {
  const [expandedCardIndex, setExpandedCardIndex] = useState(null);

  const toggleReadMore = (index) => {
    if (expandedCardIndex === index) {
      setExpandedCardIndex(null); // Collapse if clicked again
    } else {
      setExpandedCardIndex(index); // Expand this one
    }
  };

  return (
    <div className="blogs-container">
      {/* Hero Section */}
      <div className="hero">
        <h1>Welcome to Our Tips & Blogs!</h1>
        <p>Boost your productivity and skills with quick reads.</p>
      </div>

      {/* Blog Cards Section */}
      <div className="cards-wrapper">
        {blogTips.map((tip, index) => (
          <div className="card" key={index}>
            <div className="emoji">{tip.image}</div>
            <h2>{tip.title}</h2>
            <p>{tip.short}</p>
            {expandedCardIndex === index && (
              <p className="full-description">{tip.full}</p>
            )}
            <button onClick={() => toggleReadMore(index)}>
              {expandedCardIndex === index ? "Show Less" : "Read More"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blogs;
