import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './styles/Tasks.css';
import { FaPlus, FaTrash, FaEdit } from 'react-icons/fa';

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/tasks');
      setTasks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const deleteTask = async (id) => {
    if (window.confirm('Are you sure to delete this task?')) {
      try {
        await axios.delete(`http://localhost:5000/api/tasks/${id}`);
        fetchTasks();
      } catch (err) {
        console.error(err);
      }
    }
  };

  return (
    <div className="tasks-container">
      <div className="header">
        <h2>My Tasks</h2>
        <button className="create-btn" onClick={() => navigate('/create-task')}><FaPlus /></button>
      </div>

      {tasks.map((task) => (
        <div key={task._id} className="task-tab" onClick={() => setSelectedTask(task)}>
          {task.title}
        </div>
      ))}

      {selectedTask && (
        <div className="task-detail">
          <h3>{selectedTask.title}</h3>
          <p>{selectedTask.description}</p>
          <p>Status: {selectedTask.status}</p>
          <p>Date: {new Date(selectedTask.createdAt).toLocaleDateString()}</p>
          <div className="actions">
            <button onClick={() => navigate(`/edit-task/${selectedTask._id}`)}><FaEdit /> Update</button>
            <button onClick={() => deleteTask(selectedTask._id)}><FaTrash /> Delete</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Tasks;
