import React, { useEffect, useState, useRef } from "react";
import axios from "axios";

const TrackProductivity = ({ taskId, status }) => {
  const [seconds, setSeconds] = useState(0);
  const [isActive, setIsActive] = useState(true);
  const intervalRef = useRef(null);

  useEffect(() => {
    if (isActive) {
      intervalRef.current = setInterval(() => {
        setSeconds((prev) => prev + 1);
      }, 1000);
    }

    // Handle tab switching to stop the timer when the user navigates away
    const handleVisibilityChange = () => {
      if (document.hidden) {
        setIsActive(false);
        clearInterval(intervalRef.current);
      } else {
        setIsActive(true);
      }
    };

    document.addEventListener("visibilitychange", handleVisibilityChange);

    return () => {
      clearInterval(intervalRef.current);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
    };
  }, [isActive]);

  const stopAndLogTime = async () => {
    clearInterval(intervalRef.current);
    const minutes = Math.floor(seconds / 60); // Convert seconds to minutes

    if (minutes > 0) {
      try {
        const response = await axios.post(
          `/api/tasks/${taskId}/log-time`,
          { minutes },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`, // Ensure user is authenticated
            },
          }
        );
        alert(`Successfully logged ${minutes} minutes for Task ID: ${taskId}`);
        console.log(response.data);
      } catch (error) {
        console.error("Error logging time:", error);
        alert("Failed to log time.");
      }
    }

    setSeconds(0); // Reset timer after logging
  };

  const formatTime = () => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins} min ${secs} se`;
  };

  return (
    <div>
      <h3>Tracking Time for Task ID: {taskId}</h3>
      <p>Status: {status}</p>
      <p>Current Session Time: {formatTime()}</p>
      <button onClick={stopAndLogTime}>Stop & Log Time</button>
    </div>
  );
};

export default TrackProductivity;
