// components/ProductivityDisplay.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProductivityDisplay = ({ userId }) => {
  const [productivityData, setProductivityData] = useState(null);

  useEffect(() => {
    const fetchProductivity = async () => {
      try {
        const response = await axios.get(`/api/productivity/get-productivity/${userId}`);
        setProductivityData(response.data);
      } catch (err) {
        console.error('Error fetching productivity data:', err);
      }
    };

    fetchProductivity();
  }, [userId]);

  return (
    <div>
      {productivityData ? (
        <div>
          <h3>Productivity Score: {productivityData[0].productivityScore}</h3>
          <p>Last Activity: {new Date(productivityData[0].lastActivity).toLocaleString()}</p>
          <p>Activity Details: {productivityData[0].activityDetails}</p>
        </div>
      ) : (
        <p>Loading productivity data...</p>
      )}
    </div>
  );
};

export default ProductivityDisplay;
