import React, { useEffect, useState } from "react";
import { fetchInsights } from "../services/api";

const ProductivityInsights = ({ token }) => {
  const [insights, setInsights] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!token) {
      setError("Authentication token is missing.");
      setLoading(false);
      return;
    }

    fetchInsights(token)
      .then(data => {
        setInsights(data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching insights", err);
        setError("Failed to fetch insights. Please try again.");
        setLoading(false);
      });
  }, [token]);

  if (loading) return <p>Loading insights...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  if (!insights) return <p>No insights available at the moment.</p>;

  return (
    <div className="p-4 bg-white rounded-xl shadow-md">
      <h2 className="text-xl font-bold mb-3">ðŸ§  Productivity Insights</h2>
      <p><strong>Total Time Logged:</strong> {insights.totalTime || "N/A"} mins</p>
      <p>
        <strong>Most Productive Day:</strong> 
        {insights.mostProductiveDay ? (
          <>
            {insights.mostProductiveDay[0]} ({insights.mostProductiveDay[1]} mins)
          </>
        ) : "N/A"}
      </p>
      <p>
        <strong>Top Category:</strong> 
        {insights.topCategory ? (
          <>
            {insights.topCategory[0]} ({insights.topCategory[1]} mins)
          </>
        ) : "N/A"}
      </p>
      <div className="mt-4">
        <h4 className="font-semibold">Suggestions:</h4>
        <ul className="list-disc pl-5">
          {insights.advice && insights.advice.length > 0 ? (
            insights.advice.map((tip, idx) => <li key={idx}>{tip}</li>)
          ) : (
            <li>No suggestions available.</li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default ProductivityInsights;
