// components/ProductivityInsights.jsx
import React, { useEffect, useState } from "react";
import { fetchInsights } from "../services/api";

const ProductivityInsights = ({ token }) => {
  const [insights, setInsights] = useState(null);

  useEffect(() => {
    fetchInsights(token)
      .then(data => setInsights(data))
      .catch(err => console.error("Error fetching insights", err));
  }, [token]);

  if (!insights) return <p>Loading insights...</p>;

  return (
    <div className="p-4 bg-white rounded-xl shadow-md">
      <h2 className="text-xl font-bold mb-3">ðŸ§  Productivity Insights</h2>
      <p><strong>Total Time Logged:</strong> {insights.totalTime} mins</p>
      <p><strong>Most Productive Day:</strong> {insights.mostProductiveDay[0]} ({insights.mostProductiveDay[1]} mins)</p>
      <p><strong>Top Category:</strong> {insights.topCategory[0]} ({insights.topCategory[1]} mins)</p>
      <div className="mt-4">
        <h4 className="font-semibold">Suggestions:</h4>
        <ul className="list-disc pl-5">
          {insights.advice.map((tip, idx) => (
            <li key={idx}>{tip}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ProductivityInsights;
