import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Updated to useNavigate
import '../styles/Dashboard.css';
import Profile from '../components/Dashboard/Profile';
import Tasks from '../components/Dashboard/Tasks';
import TrackProductivity from '../components/Dashboard/TrackProductivity';
import ProductivityInsights from '../components/Dashboard/ProductivityInsights';
import SetGoalsandReminders from '../components/Dashboard/SetGoalsandReminders';
import GoalsandReminders from '../components/Dashboard/GoalsandReminders';
const Dashboard = () => {
  const [activeTab, setActiveTab] = useState('üë§ Profile');
  const navigate = useNavigate();  // Use useNavigate for navigation

  const handleLogout = () => {
    // Remove the authentication token from localStorage (or sessionStorage)
    localStorage.removeItem('authToken');  // Change to your token key if needed

    // Redirect the user to the home page (or login page)
    navigate('/');  // Use navigate instead of history.push()
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'üë§ Profile':
        return <Profile />;
      case 'üìã Tasks':
        return <Tasks />;
      case '‚è±Ô∏è Track Productivity':
        return <TrackProductivity />;
      case 'üìä Productivity Insights':
        return <ProductivityInsights />;

      default:
        return <Tasks />;
    }
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <h2>Dashboard</h2>
        <ul>
          {['üë§ Profile', 'üìã Tasks', '‚è±Ô∏è Track Productivity', 'üìä Productivity Insights'].map(tab => (
            <li
              key={tab}
              className={activeTab === tab ? 'active' : ''}
              onClick={() => setActiveTab(tab)}
            >
              {tab}
            </li>
          ))}
          {/* Logout Tab */}
          <li
            className="logout-tab"
            onClick={handleLogout}
          >
            üö™ Logout
          </li>
        </ul>
      </div>
      <div className="main-content">
        {renderTabContent()}
      </div>
    </div>
  );
};

export default Dashboard;
